import 'bootstrap/dist/css/bootstrap.css';
import React, { useState } from "react";
import Constants from './utilities/Constants';
import NoteCreateForm from './components/NoteCreateForm';
import NoteUpdateForm from './components/NoteUpdateForm';

export default function Home() {
  const[notes, setNotes] = useState([]);
  const[showingCreateNewNoteForm, setShowingCreateNewNoteForm] = useState(false);
  const[noteCurrentlyBeingUpdated, setNoteCurrentlyBeingUpdated]= useState(null);

  function getNotes(){
      const url = Constants.API_BASE_URL;

    fetch(url,{
      method:'GET'
    })
    .then(response => response.json())
    .then(notesFromServer => {
      setNotes(notesFromServer);
    })
    .catch((error => {
      console.log(error);
      alert(error);
    }));
  }

  function deleteNote(noteId) {
      const url = Constants.API_BASE_URL;

    fetch(url, {
      method: 'DELETE'
    })
      .then(response => response.json())
      .then(responseFromServer => {
        console.log(responseFromServer);
        onNoteDeleted(noteId);
      })
      .catch((error) => {
        console.log(error);
        alert(error);
      });
  }

  return (
    <div className="container">
      <div className="row min-vh-100">
        <div className="col d-flex flex-column justify-content-center align-items-center">
          {(showingCreateNewNoteForm === false && noteCurrentlyBeingUpdated == null) &&(
          <div>
            <h1>Notes App</h1>
            <div className='mt-5'>
              <button onClick={getNotes} className='btn btn-dark btn-lg w-100'>Get Notes From Server</button>
              <button onClick={() => setShowingCreateNewNoteForm(true)} className='btn btn-secondary btn-lg w-100 mt-4'>Create</button>
            </div>
          </div>
          )}

          {(notes.length > 0 && showingCreateNewNoteForm === false && noteCurrentlyBeingUpdated == null) && renderNotesTable()}

          {showingCreateNewNoteForm && <NoteCreateForm onNoteCreated={onNoteCreated}/>}

          {noteCurrentlyBeingUpdated !== null && <NoteUpdateForm note={noteCurrentlyBeingUpdated} onNoteUpdated={onNoteUpdated}/>}
        </div>
      </div>
    </div>
  );

  function renderNotesTable(){
    return(
      <div className="table-responsive mt-5">
        <table className="table table-bordered border-dark">
          <thead>
            <tr>
              <th scope="col">NoteId</th>
              <th scope="col">Title</th>
              <th scope="col">Content</th>
              <th scope="col">CRUD Operations</th>
            </tr>
          </thead>
          <tbody>
            {notes.map((note) => (
              <tr key={note.noteId}>
              <th scope="row">{note.noteId}</th>
              <td>{note.title}</td>
              <td>{note.content}</td>
              <td>
                <button onClick={() => setNoteCurrentlyBeingUpdated(note)} className='btn btn-dark btn-lg mx-3'>Update</button>
                <button onClick={()=> {if(window.confirm(`Are you sure you want to delete note "${note.title}?"`)) deleteNote(note.noteId)}} className='btn btn-secondary btn-lg'>Delete</button>
              </td>
            </tr>
            ))}
          </tbody>
        </table>
        <button onClick={() => setNotes([])} className='btn btn-dark btn-lg w-100'>Empty React Notes Array</button>
      </div>
    );
  }

  function onNoteCreated(createdNote){
    setShowingCreateNewNoteForm(false);    
    if (createdNote === null){
      return;
    }

    alert(`Note successfully created. After clicking OK the new note title "${createdNote.title}" will show up in the table below.`);

    getNotes();
  }

function onNoteUpdated(updatedNote) {
    setNoteCurrentlyBeingUpdated(null);

    if (updatedNote === null) {
      return;
    }

    let notesCopy = [...notes];

    const index = notesCopy.findIndex((notesCopyNote, currentIndex) => {
      if (notesCopyNote.noteId === updatedNote.noteId) {
        return true;
      }
    });

    if (index !== -1) {
      notesCopy[index] = updatedNote;
    }

    setNotes(notesCopy);

    alert(`Note successfully updated. After clicking OK, look for the note with the title "${updatedNote.title}" in the table below to see the updates.`);
  }

function onNoteDeleted(deletedNoteNoteId){
  let notesCopy = [...notes];

  const index = notesCopy.findIndex((notesCopyNote, currentIndex) => {
    if (notesCopyNote.noteId === deletedNoteNoteId) {
      return true;
    }
  });

  if (index !== -1) {
    notesCopy.splice(index,1);
  }

  setNotes(notesCopy);

  alert(`Note Successfully Delted.`);
}
}
